import { Reminder, ReminderBox } from "../Reminder/styles";
import { SearchBar, AddButton } from "./styles.js"
import React, { useState } from 'react'
// import AddBox from '/Users/johnboesen/Documents/Code/bootcamp-week1-exercises-1/personal-assistant/src/AddBox/index.js'
// import { AddButton } from 'personal-assistant/src/AddBox/styles.js'
// import Reminder from './Reminder/index.js'
// import { ReminderBox } from './Reminder/styles.js'

const Reminders = () => {
    const initialTodos = ["Click the plus button below to add a reminder!", "Click this reminder to mark it as complete!"]
    const [reminders, setReminders] = useState(initialTodos)
    const [query, setQuery] = useState('')
    const handleComplete = (todo) => {
        setReminders(reminders.splice(reminders.indexOf(todo), 1))
    };
    const search = (query) => {
        let res = new Array()
        reminders.forEach(e => {
            if (e.toLowerCase().includes(query.toLowerCase())) {
                res.push(e)
            }
        })
        setReminders(res)
    }

    return (
        <body>
            <SearchBar query={query} setQuery={setQuery}>
                <input type="text" value={query} onChange={e => { setQuery(e.target.value); search(e) }} placeholder="Search for a reminder" />
            </SearchBar>
            {/* I want this div half width and centered */}
            <div style={{
                display: 'flex', justifyContent: 'center', alignItems: 'center'
            }}>
                <ReminderBox>
                    <Reminder
                        text={"test"}
                        onClick={() => handleComplete("x")}
                    />
                    {reminders.map((todo) => (
                        <Reminder
                            text={setReminders}
                            onClick={() => handleComplete(todo)}
                        />
                    ))}
                    <AddButton>Add Reminder</AddButton>
                </ReminderBox>
            </div>
        </body>
    );
}
// ReactDOM.render(<Todos />, document.getElementById('root'));
export default Reminders
