import { SearchBar, AddButton, Reminder, ReminderBox } from "./styles.js"
import React, { useState } from 'react'
// Where do I get Alert from???
// import { Alert } from 'react-native';

const Reminders = () => {
    const initialTodos = ["Click the plus button below to add a reminder!", "Click this reminder to mark it as complete!"]
    const [reminders, setReminders] = useState(initialTodos)
    const [query, setQuery] = useState('')
    const handleComplete = (todo) => {
        setReminders(reminders.splice(reminders.indexOf(todo), 1))
    };
    const handleSearch = (query) => {
        let res = new Array()
        reminders.forEach(e => {
            if (e.toLowerCase().includes(query.toLowerCase())) {
                res.push(e)
            }
        })
        setReminders(res)
    }

    const handleAddReq = () => {
        return
        // Alert.prompt(
        //     "Enter new reminder", "Put your new reminder here...", [
        //     {
        //         text: "Cancel",
        //         onPress: () => console.log("Cancel Pressed"),
        //         style: "cancel"
        //     },
        //     {
        //         text: "OK",
        //         // Is this the right way to call this or nah???
        //         onPress: newReminder => handleAdd
        //     }
        // ],
        // )
    }


    const handleAdd = (newReminder) => {
        reminders.push(newReminder)
        setReminders(reminders)
    }

    return (
        <body>
            <SearchBar query={query} setQuery={setQuery}>
                <input type="text" value={query} onChange={e => { setQuery(e.target.value); handleSearch(e) }} placeholder="Search for a reminder" />
            </SearchBar>
            {/* I want this div half width and centered??? */}
            <div style={{
                display: 'flex', justifyContent: 'center', alignItems: 'center'
            }}>
                <ReminderBox>
                    <Reminder
                        onClick={() => handleComplete("x")}
                    > test</Reminder>
                    {/* How do I get this to work??? */}
                    {reminders.map((todo) => (
                        <Reminder
                            onClick={() => handleComplete(todo)}
                        > {todo}
                        </Reminder>
                    ))}
                    <AddButton onClick={handleAddReq()}>Add Reminder</AddButton>
                </ReminderBox>
            </div>
        </body>
    );
}
export default Reminders
