import { useReducer } from "react";
import ReactDOM from "react-dom";
import { Reminder, ReminderBox } from "../Reminder/styles";
import React, { useState } from 'react'
import AddBox from '/AddBox/index.js'
// import Reminder from './Reminder/index.js'
// import { ReminderBox } from './Reminder/styles.js'
import SearchBar from '/SearchBar/index.js'
import Greeting from '/Greeting/index.js'

const Reminders = () => {
    const initialTodos = ["Click the plus button below to add a reminder!", "Click this reminder to mark it as complete!"]
    const [reminders, setReminders] = useState(initialTodos)
    const [query, setQuery] = useState('')
    const handleComplete = (todo) => {
        reminders.splice(reminders.indexOf(reminders), 1);
    };

    return (
        <body>
            <SearchBar placeholder="Search for a reminder" query={query} setQuery={setQuery} />
            {/* I want this div half width and centered */}
            <div style={{
                display: 'flex', justifyContent: 'center', alignItems: 'center'
            }}>
                <ReminderBox>
                    {reminders.map((todo) => (
                        <Reminder
                            type="checkbox"
                            text={todo}
                            onClick={() => handleComplete(todo)}
                        />
                    ))}
                </ReminderBox>
            </div>
        </body>
    );
}
// ReactDOM.render(<Todos />, document.getElementById('root'));
export default Reminders
