import React, { useState, useEffect } from 'react'

const Greeting = () => {
    const [weather, setWeather] = useState({})
    useEffect(() => {
        const res = fetch(`https://api.openweathermap.org/data/2.5/weather?lat=41&lon=-97&appid=db5bbba816b58757082ce2230c7754a6&units=imperial`)
            .then(response => response.json())
            .then(data => { const dat = data; console.log(data.main.feels_like) })
        setWeather(dat)
    }, []);
    // console.log(weather.main.feels_like)

    return (
        <div>
            <h1>Hello!</h1>
            <h3>
                {weather ? weather.main : "Loading..."}
            </h3>
        </div>)
}

export default Greeting
