import React, { useState, useEffect } from 'react'

const Greeting = ({ query, setQuery, placeholder }) => {
    const [name, setName] = useState('')
    const [weather, setWeather] = useState({})
    useEffect(async () => {
        const res = await
            fetch(`https://api.openweathermap.org/data/2.5/weather?lat=42&lon=-97&appid=db5bbba816b58757082ce2230c7754a6&units=imperial`)
        const data = await res.json
        console.log(data)
        setWeather(data)
    }, []);

    return (
        <body>
            <h1>Hello!</h1>
            <h3>
                {weather ? weather : "Loading..."}
            </h3>
        </body>)
}


export default Greeting
