import styled from 'styled-components'

export const SearchBar = styled.form`
display: flex;
align-content: center;
flex-direction: column;
width: 50vw;
align-items: center;
`
export const Reminder = styled.button`
  background: white;
  border-right : 2px;
  border-left: 2px;
  border-radius: 2px;
  cursor: pointer;
  font-family: Arial, Helvetica, sans-serif;
  width: 50%;
  height: auto;

  &:hover {
    color: lightskyblue;
  }
`
