import styled from 'styled-components'

export const Reminder = styled.button`
  align-items: center;
  align-self: center;
  background: lightgray;
  border-bottom: 2px;
  border-top : 2px;
  border-radius: 2px;
  cursor: pointer;

  &:hover {
    color: white;
  }
`
