import React, { useState } from 'react'
import AddBox from './AddBox/index.js'
import Reminder from './Reminder/index.js'
import { ReminderBox } from './Reminder/styles.js'
import SearchBar from './SearchBar/index.js'
import Greeting from './Greeting/index.js'

// import COMPONENT from 'FILEPATH'


const App = () => {
  const initialTodos = ["Something", "something else"]
  const [query, setQuery] = useState('')
  const reducer = (state, action) => {
    switch (action.type) {
      case "COMPLETE":
        return state.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, complete: !todo.complete };
          } else {
            return todo;
          }
        });
      default:
        return state;
    }
  }
  return (
    < body >
      <SearchBar placeholder="Search for a reminder" query={query} setQuery={setQuery} />
      {/* I want this div half width and centered */}
      <div style={{
        display: 'flex', justifyContent: 'center', alignItems: 'center'
      }}>
        < ReminderBox >
          <Reminder style={{ alignItems: "center" }} text={"test"} />
          <Reminder style={{ alignItems: "center" }} text={"test"} />
          <Reminder style={{ flex: "1000", innerHeight: "1" }} text={"test"} />
          <AddBox />
        </ReminderBox>
      </div >
    </body >
  )
  return
}

export default App
