import styled from 'styled-components'

export const Reminder = styled.button`
  align-items: center;
  align-self: center;
  background: lightgray;
  border-left: 2px;
  border-right : 2px;
  border-radius: 2px;
  cursor: pointer;
  width: 50%;
  height: auto;

  &:hover {
    color: white;
  }
`
