import styled from 'styled-components'

export const div = styled.reminderBox``
export const Reminder = styled.button`
  background: white;
  border-right : 2px;
  border-radius: 2px;
  cursor: pointer;
  font-family: Arial, Helvetica, sans-serif;
  width: 50%;
  height: auto;


  &:hover {
    color: lightskyblue;
  }
`
