import styled from 'styled-components'

export const Reminder = styled.button`
  align-items: center;
  align-content: center;
  align-self: center;
  text-align: center;
  background: white;
  border-right : 2px;
  border-radius: 2px;
  cursor: pointer;
  font-family: Arial, Helvetica, sans-serif;
  width: 50%;
  height: auto;


  &:hover {
    color: lightskyblue;
  }
`
