import styled from 'styled-components'

const SearchBar = styled.form`
margin-top: 5vh;
margin-bottom: 5vh;
display: flex;
align-content: center;
flex-direction: column;
align-items: center;
display: flex;
justify-content: center;
align-items: center;
font-size: 30px;
`

const AddButton = styled.button`
display: block;
height: 200px;
background-color: transparent;
border: none;
cursor: crosshair;
flex: 1 1 auto;


&:hover {
    color: lightseagreen;
  }
`

export { AddButton, SearchBar }
